package day1.examples;

public class ExampleSwitch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int a=1;
		switch (a) {
		case 1:
			System.out.println(a);
			break;
		case 2:
			System.out.println("w");
			break;
			
		case 3:
			System.out.println("value is = " +a);
			break;


		default:
			break;
		}

	}

}
